(()=>{"use strict";document.getElementById("weather").addEventListener("click",(()=>{const t=document.getElementById("search").value;(async function(t){const n=t;try{const t=await fetch(`http://api.weatherapi.com/v1/current.json?key=2aa35394a1b649169f1102647232310&q=${n}&aqi=no`,{mode:"cors"});if(400===t.status)throw new Error("No matching location.");return{name:(e=await t.json()).location.name,country:e.location.country,temp:e.current.temp_c,condition:e.current.condition.text,wind:e.current.wind_kph}}catch(t){console.log(t)}var e})(t).then(console.log),console.log(t)}))})();